import { validateHeaderName } from 'http';
import './App.css'
import { useState } from 'react'

type Note = {
  id: number;
  title: string;
  content: string;
}

const App = () => {
  const [notes,setNotes] = useState<
  Note[]
  >([
    {
      id: 1,
      title: 'note title 1',
      content: 'content 1'
    },
    {
      id: 2,
      title: 'note title 2',
      content: 'content 2'
    },
    {
      id: 3,
      title: 'note title 3',
      content: 'content 3'
    },
    {
      id: 4,
      title: 'note title 4',
      content: 'content 4'
    }
  ]);

  const [title,setTitle] = useState('');
  const [content,setContent] = useState('');
  const [selectedNote,setSelectedNote] = useState<Note | null>(null);

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  }

  const handleAddNote = (
    event: React.FormEvent
  ) => {
    event.preventDefault();
    const newNote: Note = {
      id: notes.length +1,
      title: title,
      content: content
    };

    setNotes([newNote,...notes]);
    setTitle('');
    setContent('');
  };

  const handleUpdateNote = (
    event: React.FormEvent
  ) => {
    event.preventDefault();

    if(!selectedNote) {
      return;
    }

    const updatedNote: Note = {
      id:selectedNote.id,
      title: title,
      content: content
    }

    const updatedNotesList = notes.map((note)=>
    note.id == selectedNote.id ? updatedNote : note
    )
  }

  return (
    <div className="app-container">
      <form 
        className="note-form"
        onSubmit={(event)=>handleAddNote(event)}  
      >
        <input
        placeholder='title'
        onChange={(event)=>
          setTitle(event.target.value)
        }
        value={title}
        required/>
        <textarea
        placeholder='Content'
        rows={10}
        onChange={(event)=> {
          setContent(event.target.value)
        }}
        value={content}
        required/>
        <button type='submit'>
          Add Note
        </button>
        </form>
        <div className='notes-grid'>
          {notes.map((note)=> (
            <div 
            className='note-item'
            >
              <div className='notes-header'>
                <button>x</button>
              </div>
              <h2>{note.title}</h2>
              <p>{note.content}</p>
            </div>
          ))}
        </div>
    </div>
  )

}

export default App
